mapper.select:
select 

from message_info
where MESSAGE_ID = #{messageId,jdbcType=VARCHAR}
  and USER_ID = #{userId,jdbcType=VARCHAR}

mapper.delete:
delete from message_info
where MESSAGE_ID = #{messageId,jdbcType=VARCHAR}
  and USER_ID = #{userId,jdbcType=VARCHAR}

mapper.insert:
insert into message_info (MESSAGE_ID, USER_ID, MESSAGE_TITLE, 
  MESSAGE_BODY, CNT, CREATEUSERID, 
  CREATETIME, UPDATEUSERID, UPDATETIME
  )
values (#{messageId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{messageTitle,jdbcType=VARCHAR}, 
  #{messageBody,jdbcType=VARCHAR}, #{cnt,jdbcType=INTEGER}, #{createuserid,jdbcType=VARCHAR}, 
  #{createtime,jdbcType=TIMESTAMP}, #{updateuserid,jdbcType=VARCHAR}, #{updatetime,jdbcType=TIMESTAMP}
  )

mapper.update:
update message_info
set MESSAGE_TITLE = #{messageTitle,jdbcType=VARCHAR},
  MESSAGE_BODY = #{messageBody,jdbcType=VARCHAR},
  CNT = #{cnt,jdbcType=INTEGER},
  CREATEUSERID = #{createuserid,jdbcType=VARCHAR},
  CREATETIME = #{createtime,jdbcType=TIMESTAMP},
  UPDATEUSERID = #{updateuserid,jdbcType=VARCHAR},
  UPDATETIME = #{updatetime,jdbcType=TIMESTAMP}
where MESSAGE_ID = #{messageId,jdbcType=VARCHAR}
  and USER_ID = #{userId,jdbcType=VARCHAR}
